import styled from 'styled-components';

export const InfoSec = styled.div`
  color: red;
  padding: 160px 0;
  /* background: ${({ lightBg }) => (lightBg ? '#fff' : '#F6F7CA')}; */
  background:#fff;
`;

export const InfoRow = styled.div`
  display: flex;
  margin: 0 -15px -15px -15px;
  flex-wrap: wrap;
  align-items: center;
  /* flex-direction: ${({ imgStart }) => (imgStart ? 'row-reverse' : 'row')}; */
  flex-direction:row;
`;

export const InfoColumn = styled.div`
  margin-bottom: 15px;
  padding-right: 15px;
  padding-left: 15px;
  flex: 1;
  height:650px;
  max-width: 33.33%;
  flex-basis: 50%;

  @media screen and (max-width: 768px) {
    max-width: 100%;
    flex-basis: 100%;
    display: flex;
    justify-content: center;
  }
`;

export const TextWrapper = styled.div`
  max-width: 340px;
  padding-top: 0;
  padding-bottom: 60px;

  @media screen and (max-width: 768px) {
    padding-bottom: 65px;
  }
`;

export const ImgWrapper = styled.div`
  max-width: 600px;
  display: flex;
  justify-content: ${({ start }) => (start ? 'flex-start' : 'flex-end')};
`;

export const TopLine = styled.div`
  color: ${({ lightTopLine }) => (lightTopLine ? '#F05D23' : '#FABC3C')};
  font-size: 18px;
  line-height: 60px;
  font-weight: 700;
  letter-spacing: 1.4px;
  margin-bottom: 16px;
`;

export const Img = styled.img`
  padding-right: 0;
  border: 0;
  width: 100%;
  vertical-align: middle;
  display: inline-block;
  height: 191.25px;
`;

export const Heading = styled.h1`
  margin-bottom: 24px;
  font-size: 48px;
  line-height: 1.1;
  font-weight: 600;
  color: ${({ lightText }) => (lightText ? '#388659' : '#1c2237')};
`;

export const Subtitle = styled.p`
  max-width: ${({ wideTextDesc }) => (wideTextDesc ? '940px' : '440px')}; 
  margin-bottom: 35px;
  font-size: 18px;
  line-height: 24px;
  text-align:justify;
  color: ${({ lightTextDesc }) => (lightTextDesc ? '#48AD72' : '#1c2237')};
`;
